void main() {
  new TesterPartOne().test();
  new TesterPartTwo().test();

  print(new FloorFinder().findFloor(crazyInput));

  print(DateTime.now());
}

class FloorFinder {
  Map<String, int> findFloor(String input) {
    /*var up = "(".allMatches(input).fold(0, (int up, element) => ++up);
    int down = ")".allMatches(input).fold(0, (int down, element) => ++down);
    return up - down;*/

    int up = 0;
    int down = 0;
    bool onlyOnce = true;
    int basementHit = 0;
    for (int i = 0; input.length > i; i++) {
      if ("(" == input[i]) {
        up++;
      } else {
        down++;
      }
      print("$up - $down  = ${up - down } ");
        if (up - down == -1 && onlyOnce) {
          onlyOnce = false;
          basementHit = i + 1;
        }
    }
    print (basementHit);
    return {"exit": up - down, "basementHit": basementHit};
  }
}

class TesterPartOne {
  var tests = [
    {"input": "(())", "output": 0},
    {"input": "()()", "output": 0},
    {"input": "(((", "output": 3},
    {"input": "(()(()(", "output": 3},
    {"input": "))(((((", "output": 3},
    {"input": "())", "output": -1},
    {"input": "))(", "output": -1},
    {"input": ")))", "output": -3},
    {"input": crazyInput, "output": 232},
  ];
  test() {
    tests.forEach((Map<String, Object> map) {
      print("testing ${map["input"]} expecting ${map["output"]}");
      assert(
          new FloorFinder().findFloor(map["input"])["exit"] == map["output"]);
    });
  }
}

class TesterPartTwo {
  var tests = [
    {"input": ")", "output": 1},
    {"input": "()())", "output": 5},
		{"input": crazyInput, "output": 1783}
  ];
  test() {
    tests.forEach((Map<String, Object> map) {
      print("testing ${map["input"]} expecting ${map["output"]}");
      assert(new FloorFinder().findFloor(map["input"])["basementHit"] ==
          map["output"]);
    });
  }
}

